SELECT DATE_FORMAT(START_DATE,'%c') as MONTH, CAR_ID, COUNT(*) as RECORDS FROM CAR_RENTAL_COMPANY_RENTAL_HISTORY WHERE START_DATE BETWEEN '2022-08-01' AND '2022-10-31' GROUP BY CAR_ID,MONTH HAVING CAR_ID IN (SELECT CAR_ID FROM CAR_RENTAL_COMPANY_RENTAL_HISTORY WHERE START_DATE BETWEEN '2022-08-01' AND '2022-10-31' GROUP BY CAR_ID HAVING COUNT(*)>=5) ORDER BY DATE_FORMAT(START_DATE,'%m') ASC, CAR_ID DESC;